package ninf.MetaServer;import ninf.basic.*;import ninf.client.*;import java.io.IOException;public class ArgPosition{  public int functionIndex;  public int argNum;  public ArgPosition(NinfPacketInputStream is) throws NinfIOException{    read(is);  }  public void read(NinfPacketInputStream is) throws NinfIOException {    functionIndex = is.readInt();    argNum = is.readInt();  }  public int serialize(int argNums[]) {    return argNums[functionIndex] + argNum;  }}