package ninf.basic;import java.io.IOException;import java.io.PrintStream;public class NinfIOException extends NinfException {  IOException e;  public NinfIOException() {    super();  }  public NinfIOException(IOException e) {    super();    this.e = e;  }  public void printStackTrace(PrintStream ps){    if (e != null){      ps.println("NinfIOException: original exception follows");      e.printStackTrace(ps);    } else       super.printStackTrace(ps);  }}// end of NinfIOException.java