// NinfLog.javapackage ninf.basic;import java.io.*;public class NinfLog {  static int mode = 0;  String name;  public static PrintStream os = System.out;  public NinfLog(String className) {    name = className;  }  public NinfLog(Object obj) {    name = obj.getClass().getName();  }  public static void changeOutput(String fileName) throws NinfIOException {    try {      DataOutputStream s =	new DataOutputStream(new FileOutputStream(fileName));      os = new PrintStream(s);    }catch (IOException e){      throw new NinfIOException(e);    }  }  public static void changeOutput(PrintStream s) {    os = s;  }  public void println(String msg) {    if (mode == 0) {      os.println(name + ":" + msg);    }  }  public void println(Object o) {    if (mode == 0) {      os.println(name + ":" + o);    }  }  public void print(String msg) {    if (mode == 0) {      os.print(name + ":" + msg);    }  }  public static int changeMode(int m) {    int ret = mode;    mode = m;    return ret;  }  public static void quiet() { mode = 1; }  public static void verbose() { mode = 0; }}// end of NinfLog.java