package ninf.netsolve;// import java.io.*;// import java.awt.*;import java.awt.Frame;import java.awt.Event;import java.awt.TextArea;import java.awt.Button;import java.awt.Panel;import java.awt.BorderLayout;import java.awt.FlowLayout;import java.awt.Color;import java.awt.Font;public class TextScreen extends Frame{  private Button  close_button;  private TextArea message;  private Color default_color = new Color(50,200,240);  private Font default_font = new Font("TimesRoman",Font.BOLD,12);  private int default_height = 20;  private int default_width = 70;  public int cur_lang = Labels.language;  public TextScreen(String title, String text) {    super(title);    makeTextScreen(text,default_color,default_font,default_height,default_width);  }  public TextScreen(String title, String text, int height, int width) {    super(title);    makeTextScreen(text,default_color,default_font,height,width);  }  public TextScreen(String title, String text, Color color, int height, int width) {    super(title);    makeTextScreen(text,color,default_font,height,width);  }  public TextScreen(String title, String text, Color color, Font textFont, int height, int width) {    super(title);    makeTextScreen(text,color,textFont,height,width);  }  public TextScreen(String title, String text, Font textFont, int height, int width) {    super(title);    makeTextScreen(text,default_color,textFont,height,width);  }  public TextScreen(String title, String text, Color color) {    super(title);    makeTextScreen(text,color,default_font,default_height,default_width);  }  public TextScreen(String title, String text, Font textFont) {    super(title);    makeTextScreen(text,default_color,textFont,default_height,default_width);  }  private void makeTextScreen(String text, Color color, Font textfont,   int height, int width) {    this.setLayout(new BorderLayout(5,5));    this.setBackground(color);    Panel p1 = new Panel();    p1.setLayout(new FlowLayout(FlowLayout.CENTER,15,15));    close_button = new Button(Labels.close[cur_lang]);    close_button.setFont(new Font("TimesRoman",Font.BOLD,12));    p1.add(close_button);    message = new TextArea(height,width);    message.setFont(textfont);    message.setEditable(false);    this.add("South",p1);    this.add("North",message);    message.setText(text);    this.show();    this.pack();      }  public boolean action(Event e, Object arg) {    if (e.target == close_button) {     this.hide();     this.dispose();    }   return true;  }}