import ninf.client.*;import java.util.*;class TestGetSize {  public static void main(String args[]) {    try {      args = NinfClient.parseArg(args);      NinfClient nc = new NinfClient();      nc.callWith("getSize", x, y);      System.out.println(x, y);          } catch (Exception e) {      System.err.println("*** NINF CLIENT ERROR. ***");      e.printStackTrace();      System.exit(1);    }  }}